import {LOGIN,LOGOUT,REGISTER} from '../actions/types'

const initalState  ={
    token:localStorage.getItem('token'),
    user:null,
    isAuthenticated:false
}

export default  function(state=initalState,action){
    const {type,payload} =  action
    switch(type){
        case LOGIN:

        case LOGOUT:

        case REGISTER:
            return{
                ...state,
                ...payload

            }
        default:
            return state;
    }
}
